export const initialState = {
  user: null,
  playlists: [],
  playing: false,
  item: null,
  token:
    "BQCVHvvb50Js82985CK_tG4gfsz5TkkroPivhjSVIAeI3Xc41pBrNdZl3FRqn5W8UgBSLggLPJGtllHHU46c1Qq59yjF_FCIWO_kVjeM1Ex0-mgVBUa4z_Rw-b-e3PXV4oRSz3kZvWY0rTi9K6_nPOR-_ARzsiZ23sUx9rB-LBGSGqibpYIo",
};

const reducer = (state, action) => {
  console.log(action);
  switch (action.type) {
    case "SET_USER":
      return {
        ...state,
        user: action.user,
      };
    case "SET_TOKEN":
      return {
        ...state,
        token: action.token,
      };
    case "SET_PLAYLISTS":
      return {
        ...state,
        playlists: action.playlists,
      };
    default:
      return state;
  }
};

export default reducer;
